import React, { useState, useEffect } from 'react';
import Testimonial from './Components/Testimonial';
import axios from 'axios';
import { ArrowForwardIos, ArrowBackIos } from '@material-ui/icons';
import './App.css';

function App() {
  const [ items, setitems ] = useState([]);
  const [ isLoading, setIsLoading ] = useState(true);
  const [ itemCount, setItemCount ] = useState(0);

  useEffect(() => {
    const fetchItems = async () => {
      const result = await axios(`https://testimonialapi.toolcarton.com/api`)
      console.log(result.data)
      setitems(result.data)
      setIsLoading(false)
    }

    fetchItems()
  },[])

  function increment() {
    setItemCount(itemCount + 1)
  }
  function decrement() {
    setItemCount(itemCount - 1)
  }

  console.log(itemCount)
  return (
    <div className="App">
      <ArrowBackIos color="primary" onClick={decrement} style={{fontSize:60}} />
     <Testimonial isLoading={isLoading} items={items} />
     <ArrowForwardIos color="primary" onClick={increment} style={{fontSize:60}} />
    </div>
  );
}

export default App;
